import React,{Component} from "react";
import SearchTask from "./Components/SearchTask";
import UngroupedList from "./Components/UngroupedList";
import NewTask from "./Components/NewTask";
import TaskSituation from "./Components/TaskSituation";

class App extends Component{
    constructor(props) {
        super(props);
        this.state= {
            inputValue:'',
            list: [
                {id: '001',name: '学习React技术',done: true},
                {id: '002',name: '学习后端API',done: false}
            ]
        }
        this.sendItem = this.sendItem.bind(this)
    }
    render() {
        return(
            <div className="box">
                <SearchTask/>
                <UngroupedList
                    list = {this.state.list}
                />
                <NewTask
                    sendItem={this.sendItem}
                />
                <TaskSituation
                    listLen={this.state.list.length}
                />
            </div>
        );
    }
    sendItem(listValue){
        console.log(listValue);
        this.setState({
            list:[...this.state.list,this.state.inputValue=listValue]
        })
    }
}
export default App;
