import React,{Component} from "react";
import SearchTask from "./Components/SearchTask";
import UngroupedList from "./Components/UngroupedList";
import NewTask from "./Components/NewTask";
import TaskSituation from "./Components/TaskSituation";

class App extends Component{
    constructor(props) {
        super(props);
        this.state= {
            list: [
                {id: '001',name: '学习React技术',done: true},
                {id: '002',name: '学习后端API',done: false}
            ]
        }
        this.sendItem = this.sendItem.bind(this)
    }
    render() {
        const {list} = this.state;
        return(
            <div className="box">
                <SearchTask/>
                <UngroupedList
                    list = {list}
                />
                <NewTask
                    sendItem={this.sendItem}
                />
                <TaskSituation
                    listLen={list.length}
                />
            </div>
        );
    }
    sendItem(todoObj){
        const {list} = this.state;
        const newList = [...list,todoObj]
        console.log(todoObj);
        this.setState({
            list:newList
        })
        // this.setState({
        //     list:[...this.state.list,this.state.inputValue=data]
        // })
    }
}
export default App;
