import React,{Component} from "react";

class NewTask extends Component{
    constructor(props) {
        super(props);
        this.state = {
            showElem:true
        }
        // this.inputChange = this.inputChange.bind(this);
        this.addList = this.addList.bind(this);
        this.showBox = this.showBox.bind(this);
    }

    render() {
        const showBox = this.state.showElem;
        return(
            <div className="boxNew">
                <button
                    className="newBtn"
                    onClick={this.showBox}
                    style={{
                        display: showBox ? 'block' : 'none'
                    }}
                >
                    新建任务
                </button>
                <div
                    className="newBox"
                    style={{
                        display: showBox ? 'none' : 'block'
                    }}
                >
                    <label htmlFor="boxText">新建任务:</label>
                    <input
                        id="boxText"
                        ref={c => this.boxText = c}
                        className="boxText"
                        type="text"
                    />
                    <button
                        className="boxBtn"
                        onClick={this.addList}
                    >
                        保存
                    </button>
                </div>
            </div>
        );
    }
    showBox(){
        this.setState({
            showElem:!this.state.showElem
        })
    }
    addList(){
        if(this.boxText.value === '')
            alert("添加任务不能为空");
        else {
            alert(this.boxText.value)
            // this.props.sendItem(this.state.inputValue);
            // this.setState({
            //     inputValue:' '
            // })
        }
    }
}
export default NewTask;
