import React,{Component} from "react";
import SearchTask from "./Components/SearchTask";
import List from "./Components/List";
import NewTask from "./Components/NewTask";
import TaskSituation from "./Components/TaskSituation";
import "./App.css";

class App extends Component{
    constructor(props) {
        super(props);
        this.state= {
            list: [
                {id: '1学习React技术', name: '学习React技术', done: true, grouping: '未分组', tag: '紧急', tagDone:true},
                {id: '2学习后端API', name: '学习后端API', done: false, grouping: '未分组', tag: null, tagDone:false},
                {id: '3完成任务列表', name: '完成任务列表', done: false, grouping: '明天', tag: null, tagDone:false},
                {id: '4完成删除任务', name: '完成删除任务', done: false, grouping: '明天', tag: null, tagDone:false},
                {id: '5完成搜索任务', name: '完成搜索任务', done: false, grouping: '明天', tag: null, tagDone:false},
                {id: '6完成任务分租', name: '完成任务分租', done: false, grouping: '明天', tag: null, tagDone:false},
                {id: '7完成任务打标签', name: '完成任务打标签', done: false, grouping: '明天', tag: null, tagDone:false},
            ],
            group:['未分组','明天'],
        }
        this.addItem = this.addItem.bind(this)
        this.toggleDoneItem = this.toggleDoneItem.bind(this)
        this.deleteList = this.deleteList.bind(this)
        this.search = this.search.bind(this)
    }
    render() {
        const {list,group} = this.state;
        return(
            <div className="box">
                <SearchTask
                    search={this.search}
                />
                <div className="boxList">
                    {
                        group.map( item => {
                            return (
                                <List
                                    key={item}
                                    groupName = {item}
                                    list = {list}
                                    toggleDoneItem = {this.toggleDoneItem}
                                    deleteList = {this.deleteList}
                                />
                            )
                        })
                    }
                </div>
                <NewTask
                    addItem={this.addItem}
                    len = {list.length}
                />
                <TaskSituation
                    list = {list}
                />
            </div>
        );
    }
    // 增加list中的item
    addItem(listObj){
        // 获取list
        const {list,group} = this.state;
        // 将list与接受参数进行组合并新建一个
        const newList = [...list,listObj];
        const newGrouping = group.includes(listObj.grouping) ? [...group] : [...group,listObj.grouping];
        console.log(listObj);
        // 数据更新
        this.setState({
            list:newList,
            group:newGrouping
        })
    }
    // 改变list.done的状态
    toggleDoneItem(id,done){
        const {list} = this.state;
        const newListObj = list.map((listObj) => {
            if(listObj.id === id)
                return {...listObj,done}
            else
                return listObj
        })
        this.setState({
            list:newListObj
        })
    }
    // 删除选中的item
    deleteList(id,grouping){
        const {list} = this.state;
        const newListObj = list.filter((listObj) => {
            return listObj.id !== id
        })
        const newGroupObj= list.filter(groupObj => groupObj.grouping === grouping).length - 1;

        if (newGroupObj === 0){
            console.log(this.state.group.splice(this.state.group.indexOf(grouping),1));
        }

        this.setState({
            list:newListObj,
            group:this.state.group
        })
    }

    search(Value){
        console.log(Value);
        const {list,listTwo} = this.state;
        list.filter(valueItem => valueItem.name.indexOf(Value) !== -1)
            .map(v => {
                let re = new RegExp(Value,"g");
                return (
                    v.name = v.name.replace(re,`<span class="keyHigh">${Value}</span>`),
                        console.log(v),
                        this.setState({
                            list:[...list]
                        })
                )
            })
    }
}
export default App;
