import React,{Component} from "react";
import TitleName from "./Assembly/TitleName";
import SearchTask from "./Assembly/SearchTask";
import UngroupedTasks from "./Assembly/UngroupedTasks";
import NewTask from "./Assembly/NewTask";
import TaskSituation from "./Assembly/TaskSituation";

class App extends Component{
    constructor(props) {
        super(props);
        this.state= {
            inputValue:'',
            list: ['学习React技术', '学习后端API']
        }
        this.sendItem = this.sendItem.bind(this)
    }
    render() {
        return(
            <div className="box">
                <TitleName/>
                <SearchTask/>
                <UngroupedTasks
                    list = {this.state.list}
                />
                <NewTask
                    sendItem={this.sendItem}
                />
                <TaskSituation
                    listLen={this.state.list.length}
                />
            </div>
        );
    }
    sendItem(listValue){
        console.log(listValue);
        this.setState({
            list:[...this.state.list,this.state.inputValue=listValue]
        })
    }
}
export default App;
