import React,{Component} from "react";
import SearchTask from "./Components/SearchTask";
import UngroupedList from "./Components/UngroupedList";
import NewTask from "./Components/NewTask";
import TaskSituation from "./Components/TaskSituation";

class App extends Component{
    constructor(props) {
        super(props);
        this.state= {
            list: [
                {id: '1学习React技术',name: '学习React技术',done: true,grouping: null},
                {id: '2学习后端API',name: '学习后端API',done: false,grouping: null}
            ]
        }
        this.addItem = this.addItem.bind(this)
        this.toggleDoneItem = this.toggleDoneItem.bind(this)
        this.deleteList = this.deleteList.bind(this)
    }
    render() {
        const {list} = this.state;
        return(
            <div className="box">
                <SearchTask/>
                <UngroupedList
                    list = {list}
                    toggleDoneItem = {this.toggleDoneItem}
                    deleteList = {this.deleteList}
                />
                <NewTask
                    addItem={this.addItem}
                    len = {list.length}
                />
                <TaskSituation
                    listLen={list.length}
                />
            </div>
        );
    }
    addItem(listObj){
        // 获取list
        const {list} = this.state;
        // 将list与接受参数进行组合并新建一个
        const newList = [...list,listObj]
        console.log(listObj);
        // 数据更新
        this.setState({
            list:newList
        })
    }

    toggleDoneItem(id,done){
        const {list} = this.state;
        const newListObj = list.map((listObj) => {
            if(listObj.id === id)
                return {...listObj,done}
            else
                return listObj
        })
        this.setState({
            list:newListObj
        })
    }

    deleteList(id){
        const {list} = this.state;
        const newListObj = list.filter((listObj) => {
            return listObj.id !== id
        })
        this.setState({
            list:newListObj
        })
    }
}
export default App;
