import React,{Component} from "react";

class NewTask extends Component{
    constructor(props) {
        super(props);
        this.state = {
            inputValue:'',
            list: ['学习React技术', '学习后端API'],
            showElem:true
        }
    }

    render() {
        const showBox = this.state.showElem;
        return(
            <div className="boxNew">
                <button
                    className="newBtn"
                    onClick={this.showBox}
                    // style={{
                    //     display: showBox ? 'block' : 'none'
                    // }}
                >
                    新建任务
                </button>
                <div
                    className="newBox"
                    // style={{
                    //     display: showBox ? 'none' : 'block'
                    // }}
                >
                    <label htmlFor="boxText">新建任务:</label>
                    <input
                        id="boxText"
                        ref={c => this.boxText = c}
                        className="boxText"
                        type="text"
                        value={this.state.inputValue}
                        onChange={this.inputChange}
                    />
                    <button
                        className="boxBtn"
                        onClick={this.addList}
                    >
                        保存
                    </button>
                </div>
            </div>
        );
    }
    showBox = ()=>{
        this.setState({
            showElem:!this.state.showElem
        })
    }
    inputChange = ()=>{
        this.setState({
            inputValue:this.boxText.value
        })
    }
    addList = ()=>{
        const {send} = this.props;
        send(this.state.list);
        if (this.state.inputValue === '')
            alert("添加任务不能为空");
        else {
            this.setState({
                list:[...this.state.list,this.state.inputValue],
                inputValue:'',
                showElem:!this.state.showElem
            })
        }
    }
}
export default NewTask;
